import Game = require("game");
import IScene = require("iScene");
import KYScene = require("KYScene");
import Rumia = require("rumia");
import Item = require("item");
import C = require("const");
import Star = require("star");


enum Fruit { GRAPE, APPLE, BANANA }


class Play extends KYScene implements IScene {
    private count: number;
    private score: number;
    private hiScore: number;
    private rumia: Rumia;
    private itemList: Array<Item>;
    private currentTime: number;
    private nextScene: string;
    private whiteDepth: number;
    private itemInterval: number;
    private itemNextInterval: number;
    private nextItem: Fruit;
    private setHiScore: Function;
    private getHiScore: Function;
    private starList: Array<Star>;
    private gameOver: boolean;
    private gameOverY: number;
    private gameOverVY: number;
    private pause: boolean;
    private span: Span;

    constructor(game: Game) {
        super(game);
        this.itemList = new Array(C.ITEM_MAX);
        this.setHiScore = game.setHiScore;
        this.getHiScore = game.getHiScore;
        this.starList = new Array(C.ITEM_MAX);
    }

    public init = (): void => {
        this.count = 0;
        this.score = 0;
        this.hiScore = this.getHiScore();
        this.currentTime = -1;
        this.nextScene = "";
        this.whiteDepth = 1;
        this.itemInterval = Math.round(Math.random() * C.BASE_ITEM_INTERVAL);
        this.itemNextInterval = C.BASE_ITEM_INTERVAL - this.itemInterval;
        var rnd: number = Math.random() * 3;
        if (rnd > 2) this.nextItem = Fruit.GRAPE;
        else if (rnd > 1) this.nextItem = Fruit.APPLE;
        else this.nextItem = Fruit.BANANA;
        this.rumia = new Rumia(this.keyControl, this.se, this.ctx, this.image, this.scoreUp, this.setGameOver);
        this.rumia.init();
        this.gameOver = false;
        this.pause = false;
        this.span = new Span(this.addStar);
        for (var i: number = 0; i < C.STAR_MAX; i++) {
            this.starList[i] = new Star(this.ctx, this.image);
        }
        for (var i: number = 0; i < C.ITEM_MAX; i++) {
            this.itemList[i] = new Item(this.ctx, this.image);
        }
    };

    public calc = (): void => {
        if (this.pause) {
            if (this.keyControl.getKey("P").getCount() == 1) {
                this.se.play("pause");
                this.pause = false;
            }
            return;
        }
        if (this.keyControl.getKey("P").getCount() == 1) {
            this.se.play("pause");
            this.pause = true;
        }
        if (this.count == 0) this.music.play("bgm2");
        if (this.count <= 30) {
            this.whiteDepth -= 1 / 30;
            this.music.changeVolume("bgm2", this.count / 30);
        }
        if (!this.gameOver) this.createItem();
        if (this.count % 2080 == 0) this.span.init();
        this.span.calc();

        for (var i: number = 0; i < C.STAR_MAX; i++) {
            this.starList[i].calc();
        }
        for (var i: number = 0; i < C.ITEM_MAX; i++) {
            this.itemList[i].calc();
        }
        this.rumia.calc();

        for (var i: number = 0; i < C.ITEM_MAX; i++) {
            if (this.rumia.isHittingItem(this.itemList[i])) {
                this.itemList[i].onHit(this.rumia);
            }
            this.rumia.jadgeExpectationItem(this.itemList[i]);
        }
        for (var i: number = 0; i < C.STAR_MAX; i++) {
            if (this.rumia.isHittingStar(this.starList[i])) {
                this.starList[i].onHit(this.rumia);
            }
        }
        if (this.score > this.hiScore) this.hiScore = this.score;
        if (this.gameOver) {
            if (this.currentTime == -1) {
                this.currentTime = this.count;
                this.gameOverY = 180;
                this.gameOverVY = 0;
                this.music.stop("bgm2");
            }
            if ((this.count - this.currentTime) % 70 == 0 && (this.count - this.currentTime) < 210) {
                this.se.play("gameover");
            }
            if ((this.count - this.currentTime) < 120) {
                this.gameOverVY += 0.2;
                if (this.gameOverY >= 408) {
                    this.gameOverVY = -9.5;
                }
                this.gameOverY += this.gameOverVY;
            }
            if ((this.count - this.currentTime) > 300) {
                this.whiteDepth = (this.count - this.currentTime - 300) / 30;
            }
            if ((this.count - this.currentTime - 300) == 30) {
                this.changeScene("title");
                this.setHiScore(this.hiScore);
            }
        }
    };

    public graph = (): void => {
        for (var i: number = 0; i < 5; i++) {
            this.image.draw(this.ctx, "bg", (i * 144) - (this.count % 288) / 2, 42);
        }

        for (var i: number = 0; i < C.ITEM_MAX; i++) {
            this.itemList[i].graph();
        }
        this.rumia.graph();
        for (var i: number = 0; i < C.STAR_MAX; i++) {
            this.starList[i].graph();
        }

        if (this.gameOver) {
            this.ctx.font = "bold 32px 'Gulim'"
            this.ctx.fillStyle = "rgb(255,0,0)";
            this.ctx.fillText("GAME OVER", 140, this.gameOverY);
        }
        this.graphBar();

        if (this.whiteDepth > 0) {
            this.ctx.globalAlpha = this.whiteDepth;
            this.ctx.fillStyle = "rgb(255,255,255)";
            this.ctx.fillRect(0, 0, 640, 480);
            this.ctx.globalAlpha = 1;
        }
    };

    private graphBar = (): void => {
        this.ctx.fillStyle = "rgb(0,0,0)";
        this.ctx.fillRect(0, 0, 480, 72);
        this.ctx.fillRect(0, 408, 480, 24);

        this.ctx.font = "bold 20px 'Gulim'"
        this.ctx.fillStyle = "rgb(255,255,255)";
        var num: number;
        this.image.draw(this.ctx, "HI-", 136, 6);
        this.image.draw(this.ctx, "SCORE", 166, 6);
        if (this.hiScore < 100000) {
            num = this.hiScore;
            for (var i: number = 0; i < 5; i++) {
                this.ctx.fillText(String(num % 10), 275 - (14 * i), 25);
                num = Math.floor(num / 10);
            }
        }
        else {
            this.ctx.fillText(this.hiScore.toString(), 220, 25);
        }
        this.image.draw(this.ctx, "SCORE", 6, 42);
        if (this.score < 100000) {
            num = this.score;
            for (var i: number = 0; i < 5; i++) {
                this.ctx.fillText(String(num % 10), 115 - (14 * i), 61);
                num = Math.floor(num / 10);
            }
        }
        else {
            this.ctx.fillText(this.score.toString(), 60, 61);
        }

        this.image.draw(this.ctx, "heart", 291, 41);
        this.ctx.fillRect(325, 43, 132, 20);
        this.ctx.fillStyle = "rgb(0,0,0)";
        this.ctx.fillRect(327, 45, 128, 16);
        if (this.rumia.getOnaka() > 0) this.ctx.drawImage(this.image.get("onaka"), 128 - (this.count % 256) / 2, 0, 128 * this.rumia.getOnaka() / C.ONAMA_MAX, 16, 327, 45, 128 * this.rumia.getOnaka() / C.ONAMA_MAX, 16);

        if (this.rumia.getLife() > 0) {
            for (var i: number = 0; i < this.rumia.getLife(); i++) {
                this.image.draw(this.ctx, "zanki", 25 * i + 1, 408);
            }
        }

    };

    private createItem = (): void => {
        if (this.itemInterval == 0) {
            for (var i: number = 0; i < C.ITEM_MAX; i++) {
                if (!this.itemList[i].isFlag()) {
                    var rnd: number = Math.random() * 100;
                    switch (this.nextItem) {
                        case Fruit.BANANA:
                            this.itemList[i].init(504, this.rumia.getY(), "f2_48", C.BANANA_GAIN);
                            if (rnd > 90) this.nextItem = Fruit.BANANA;
                            else if (rnd > 60) this.nextItem = Fruit.APPLE;
                            else this.nextItem = Fruit.GRAPE;
                            break;
                        case Fruit.APPLE:
                            this.itemList[i].init(504, this.rumia.getY(), "f1_48", C.APPLE_GAIN);
                            if (rnd > 60) this.nextItem = Fruit.BANANA;
                            else if (rnd > 20) this.nextItem = Fruit.APPLE;
                            else this.nextItem = Fruit.GRAPE;
                            break;
                        case Fruit.GRAPE:
                            this.itemList[i].init(504, this.rumia.getY(), "f3_48", C.GRAPE_GAIN);
                            if (rnd > 90) this.nextItem = Fruit.GRAPE;
                            else if (rnd > 60) this.nextItem = Fruit.APPLE;
                            else this.nextItem = Fruit.BANANA;
                            break;
                    }
                    break;
                }
            }
            if (this.itemNextInterval == 0) {
                this.itemInterval = Math.round(Math.random() * C.BASE_ITEM_INTERVAL);
                this.itemNextInterval = C.BASE_ITEM_INTERVAL - this.itemInterval;
            }
            else {
                this.itemInterval = this.itemNextInterval;
                this.itemNextInterval = 0;
            }
        }
        if (this.itemInterval > 0) this.itemInterval--;
    };

    public countUp = (): void => {
        if (this.pause) return;
        this.count++;
    };

    public scoreUp = (): void => {
        this.score++;
    };

    public setGameOver = (): void => {
        this.gameOver = true;
    };

    public addStar = (large: boolean, x: number, y: number, shooting: boolean = false): void=> {
        for (var i: number = 0; i < C.STAR_MAX; i++) {
            if (!this.starList[i].isFlag()) {
                this.starList[i].init(large, x, y, shooting);
                break;
            }
        }
    };
}
export = Play;

